import React from 'react';

const publications = [
  {
    title: 'Psychological Trait Analysis Using Tweets (IEEE InC4 \'24)',
    tech: 'Python, Deep Learning, NLP',
    tags: ['#NLP', '#MentalHealth', '#DeepLearning', '#Publication'],
    description:
      'Co-authored a peer-reviewed paper accepted at IEEE InC4 2024. Proposed a hybrid CNN-LSTM model to detect psychological traits from Twitter data with 99%+ accuracy.',
    link: 'https://ieeexplore.ieee.org/document/10574935',
  },
];

const projects = [
  {
    title: 'Previous Contact and Subscription Rates',
    tech: 'Python, Scikit-learn, Causal Inference',
    tags: ['#Marketing', '#CausalInference', '#Banking'],
    description:
      'Causal analysis of bank marketing data to estimate the effect of prior outreach on subscription likelihood. Used PSM and Causal Forests to control for bias.',
    link: 'https://github.com/utkarshg06/Previous_Contact_and_Subscription_Rates_Market_Analytics',
  },
  {
    title: 'Duke Health – CMS Coverage & Health Outcomes',
    tech: 'R, SQL, Tableau',
    tags: ['#Healthcare', '#Policy', '#CausalInference'],
    description:
      'Capstone with Duke Health to assess Medicaid expansion’s impact on chronic disease outcomes. Used DiD and fixed-effects models to derive policy-level insights.',
    link: 'https://github.com/utkarshg06/Duke_Health_CMS_Coverage_Health_Outcomes',
  },
  {
    title: 'Pyramyd.AI – Predictive Bidding Engine',
    tech: 'Python, LightGBM, FastAPI',
    tags: ['#AdTech', '#ML', '#FastAPI'],
    description:
      'Designed an internal AI workflow demo for Pyramyd.AI, showcasing image collaging from user inputs using segmentation models. Built a responsive UI with automated backend processing to highlight real-time media manipulation capabilities.',
    link: 'https://github.com/lavyam/PyramydAI-API',
  },
  {
    title: 'Major Project – Psychological Trait Detection',
    tech: 'Python, Deep Learning, NLP',
    tags: ['#MentalHealth', '#NLP', '#DeepLearning'],
    description:
      'Developed a hybrid LSTM-CNN model to identify depression and anxiety indicators from tweets. Achieved over 99% classification accuracy.',
    link: 'https://github.com/kartiksingh11/Major_Project',
  },
];

const ProjectCard = ({ title, tech, tags, description, link, type }) => (
  <div className="border border-gray-300 dark:border-gray-600 rounded-xl p-6 shadow-sm hover:shadow-md transition bg-white dark:bg-gray-800">
    <h3 className="text-lg font-semibold text-indigo-700 dark:text-indigo-300 mb-1">{title}</h3>
    <p className="text-sm text-gray-500 dark:text-gray-400 mb-2">{tech}</p>
    <p className="text-sm text-gray-700 dark:text-gray-300 mb-3">{description}</p>
    <div className="flex flex-wrap gap-2 mb-4">
      {tags.map((tag, i) => (
        <span
          key={i}
          className="bg-indigo-100 dark:bg-indigo-700 text-indigo-800 dark:text-white px-2 py-0.5 rounded-full text-xs font-medium"
        >
          {tag}
        </span>
      ))}
    </div>
    <a
      href={link}
      target="_blank"
      rel="noopener noreferrer"
      className="inline-block px-4 py-2 text-sm font-medium bg-indigo-600 text-white rounded hover:bg-indigo-700 transition"
    >
      {type === 'pub' ? 'View Publication ↗' : 'View Project ↗'}
    </a>
  </div>
);

const Projects = () => (
  <section className="max-w-6xl mx-auto px-6 py-12 space-y-12">
    {/* Publications */}
    <div>
      <h2 className="text-3xl font-bold mb-6">Publications</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
        {publications.map((pub, idx) => (
          <ProjectCard key={idx} {...pub} type="pub" />
        ))}
      </div>
    </div>

    {/* Projects */}
    <div>
      <h2 className="text-3xl font-bold mb-6">Projects</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
        {projects.map((proj, idx) => (
          <ProjectCard key={idx} {...proj} type="project" />
        ))}
      </div>
    </div>
  </section>
);

export default Projects;
